/*global momentum, bvConfig, angular*/
momentum.factory('rule', ['category', '$q', '$http',
    function (category, $q, $http) {
        var rule = {},
            moduleMap = {
                'slack|alert_make': 'top_not_alerted',
                'promotion_stop|alert_remove': 'alerted',
                'promotion_start': 'top_not_promoted',
                'promotion_stop|promotion_finish': 'promoted'
            };

        function getKey (item) {
            var ret = [];

            [
                item.cat1name,
                item.cat2name,
                item.cat3name
            ].forEach(function (cat) {
                if (cat !== 'ALL') {
                    ret.push(decodeURIComponent(cat));
                }
            });

            return ret.length && ret.join(', ') || 'Global';
        }

        function transform (items) {
            var ret = [];

            items.forEach(function (item) {
                var root = item.definition.OR[0].momentum,
                    actions = item.definition.action.slice(0),
                    act;

                if (item.deleted) {
                    return;
                }

                actions.sort(function (a, b) {
                    return a < b;
                });

                act = {
                    'id': item.id,
                    'value': root[Object.keys(root)[0]],
                    'condition': Object.keys(root)[0],
                    'action': actions.join('|'),
                    'options': Object.keys(item.options).length &&
                        item.options || {},
                    'my': Number(item.my),
                    'name': item.name
                };

                act.$original = angular.copy(act);

                ret.push(act);
            });

            return ret;
        }

        function group (items, cats) {
            var g = {},
                ret = [],
                activeGroup;

            function transformCats (cats) {
                var i = 0,
                    ret = cats.slice(0);

                for (; i < ret.length; i += 1) {
                    if (ret[i] === 'all') {
                        ret[i] = 0;
                    }
                }

                return ret;
            }

            cats = transformCats(cats);

            items.forEach(function (item) {
                var key = getKey(item);

                g[key] = g[key] || [];

                g[key].push(item);
            });

            Object.keys(g).forEach(function (group) {
                ret.push({
                    'id': group,
                    'items': g[group],
                    'cat1': g[group][0].cat1,
                    'cat2': g[group][0].cat2,
                    'cat3': g[group][0].cat3
                });
            });

            ret.sort(function (a, b) {
                if (a === 'Global' || b === 'Global') {
                    return 1;
                }

                return a > b;
            });

            ret.forEach(function (group) {
                group.items = transform(group.items);
                if (group.cat1 === cats[0] &&
                    group.cat2 === cats[1] &&
                    group.cat3 === cats[2]) {
                    activeGroup = group;
                }
            });

            ret.splice(ret.indexOf(activeGroup), 1);

            activeGroup.active = 1;

            return {
                'active': activeGroup,
                'groups': ret
            };
        }

        rule.list = function (sessionId) {
            return category.getWithSelected(
                sessionId
            ).then(function (data) {
                return $http.post([
                    bvConfig.endpoint,
                    'rule/list'
                ].join(''), {
                    'session_id': sessionId
                }).then(function (res) {
                    res.data.forEach(function (act) {
                        act.definition = JSON.parse(
                            act.definition
                        );
                        act.options = JSON.parse(
                            act.options
                        );
                    });
                    return group(res.data, [
                        data.category.selected,
                        data.subCategory.selected,
                        data.subSubCategory.selected
                    ]);
                });
            });
        };

        function transformItem (group, item) {
            var req = {};

            req.id = item.id || '';
            req.name = item.name || '';
            req.cat1 = group.cat1;
            req.cat2 = group.cat2;
            req.cat3 = group.cat3;
            req.definition = {
                'OR': [
                    {
                        'momentum': {}
                    }
                ],
                'action': item.action.split('|')
            };

            req.definition.OR[0].momentum[item.condition] = item.value;

            req.definition = JSON.stringify(req.definition, null, 4);

            req.module = moduleMap[item.action];

            req.my = item.my;

            if (Object.keys(item.options).length) {
                req.options = JSON.stringify(item.options, null, 4);
            }

            return req;
        }

        rule.saveGroup = function (sessionId, group) {
            var newElements = [],
                deletedElements = [],
                updatedElements = [];

            group.items.forEach(function (item) {
                if (!item.$original) {
                    newElements.push(transformItem(group, item));
                    return;
                }

                if (item.deleted) {
                    deletedElements.push({
                        'id': item.id
                    });
                    return;
                }

                if (!angular.equals(item, item.$original)) {
                    updatedElements.push(transformItem(group, item));
                    return;
                }
            });

            if (!newElements.length &&
                !deletedElements.length &&
                !updatedElements.length) {
                return $q.resolve();
            }

            return $http.post([
                bvConfig.endpoint,
                'rule/save'
            ].join(''), {
                'session_id': sessionId,
                'data': {
                    'add': newElements,
                    'delete': deletedElements,
                    'update': updatedElements
                }
            }).then(function (res) {
                return res.data;
            });
        };

        return rule;
    }
]);

/*global momentum, CryptoJS, localStorage, window*/
momentum.factory('storage', ['$q', '$cookies',
function ($q, $cookies) {
    var storage = {};

    function get (key) {
        return localStorage[key];
    }

    function getObject (key) {
        var data = null;

        try {
            data = JSON.parse(localStorage[key]);
        } catch (ignore) {
            //ignore
        }

        return data;
    }

    function remove (key) {
        return localStorage.removeItem(key);
    }

    function put (key, value) {
        localStorage[key] = String(value);
    }

    function putObject (key, object) {
        localStorage[key] = JSON.stringify(object);
    }

    storage.storeAuthData = function (sessionId, teams) {
        return $q(function (resolve) {
            put('BVSID', sessionId);
            $cookies.put('BVSID', sessionId);

            if (teams) {
                putObject('BVTEAMS', teams);
                storage.setSelectedTeam(teams[0].id);
            }
            resolve();
        });
    };

    storage.getSessionId = function () {
        return $q(function (resolve) {
            var sid = get('BVSID') || '0';

            resolve(sid);
        });
    };

    storage.clearAuthData = function () {
        return $q(function (resolve) {
            $cookies.remove('BVSID');
            remove('BVSID');
            remove('BVUSER');
            remove('BVTEAMS');
            remove('BVSELECTEDTEAM');
            remove('BVFILTERS');
            resolve();
        });
    };

    storage.getUser = function () {
        return $q(function (resolve, reject) {
            var user = getObject('BVUSER');

            if (!user) {
                return reject('storage:401:No user');
            }
            user.md5email = CryptoJS.MD5(user.email).toString();
            user.title = user.is_admin ? 'Administrator' : 'User';
            user.name = user.email;
            resolve(user);
        });
    };

    storage.isExtensionInstalled = function () {
        return $cookies.get('bv_extension');
    };

    storage.getTeams = function () {
        return $q(function (resolve) {
            var teams = getObject('BVTEAMS') || 0;

            resolve(teams);
        });
    };

    storage.getSelectedTeam = function () {
        return $q(function (resolve) {
            var team = getObject('BVSELECTEDTEAM') || 0;

            resolve(team);
        });
    };

    storage.setSelectedTeam = function (team) {
        return $q(function (resolve) {
            putObject('BVSELECTEDTEAM', team);
            resolve();
        });
    };

    storage.getCache = function () {
        return localStorage['cache'] || '0';
    };

    storage.invalidateCache = function () {
        localStorage['cache'] = String(Date.now());
        window.parent.postMessage('invalidateCache', '*');
    };

    storage.getDemo = function () {
        return Number(get('is_demo')) || 0;
    };

    storage.setDemo = function (value) {
        return put('is_demo', value);
    };

    return storage;
}]);

